mapper.insert:
INSERT INTO t_activity_type(
	`name`,
	sequence,
	is_deleted
) VALUES (
	#{name},
	#{sequence},
	#{deleted.code}
)

mapper.update:
UPDATE t_activity_type


		name = #{name},


		sequence = #{sequence},


		is_deleted = #{deleted.code}


WHERE id = #{id}

mapper.select:
SELECT , ,
FROM t_activity_user au INNER JOIN t_user u ON u.id = au.user_id INNER JOIN t_activity aa ON aa.id = au.activity_id
au.activity_id = #{filter.activityId}
AND u.name LIKE #{filter.userName}
ORDER BY au.create_date ASC
limit #{filter.start}, #{filter.size}

SELECT COUNT(*) FROM t_activity_user au INNER JOIN t_user u ON u.id = au.user_id INNER JOIN t_activity aa ON aa.id = au.activity_id
au.activity_id = #{filter.activityId}
AND u.name LIKE #{filter.userName}

SELECT
FROM t_activity_type u WHERE u.is_deleted = 'N' ORDER BY u.sequence ASC
limit #{filter.start}, #{filter.size}

SELECT , , ,
FROM t_activity aa INNER JOIN t_activity_type u ON aa.type_id = u.id INNER JOIN t_address_detail ad ON ad.id = aa.address_detail_id INNER JOIN t_address a ON a.id = ad.address_id
aa.title LIKE #{filter.title}
AND aa.type_id LIKE #{filter.typeId}
AND aa.is_online = #{filter.online.code}
AND aa.end_time > NOW()
ORDER BY aa.end_time DESC
limit #{filter.start}, #{filter.size}

SELECT COUNT(*) FROM t_activity aa INNER JOIN t_activity_type u ON aa.type_id = u.id INNER JOIN t_address_detail ad ON ad.id = aa.address_detail_id INNER JOIN t_address a ON a.id = ad.address_id
aa.title LIKE #{filter.title}
AND aa.type_id LIKE #{filter.typeId}
AND aa.is_online = #{filter.online.code}
AND aa.end_time > NOW()
